testcase = ""

start = 0
end = 4
globindex = 1
index = 0
while end<=len(testcase):
    sample = testcase[start:end]
    checkrepeat = []
    for i in sample:
        if i not in checkrepeat:
            checkrepeat.append(i)
            index = globindex
        else:
            break
    if checkrepeat == list(sample):
        index+=3
        break
    else:
        start+=1
        end+=1
    globindex+=1
print(index)